    overridedMaterials.afterEdit = CLOUD.MaterialUtil.createPhongMaterial({ color: 0xFABD05, opacity: 1, transparent: true, side: THREE.DoubleSide });

    var materialOverriderByUserId = {};

    var materialOverriderByUserData = {};

